n = int(input())
a = list(map(int, input().split()))

a_sum = sum(a)

res = []

for i in range(n):
    if a[i] == a_sum - a[i]:
        res.append(i + 1)

print(len(res))
if len(res) > 0:
    print(*res)

n = int(input())
a = list(map(int, input().split()))
s = sum(a)
d = {}
for i in range(n):
    d[i+1] = s - a[i]
d = {key:val for key,val in d.items() if val == a[key-1]}
print(len(d))
if len(d) > 0:
    print(*d.keys())
