def main():
    n = int(input())
    a = [int(i) for i in input().split()]
    a_min = min(a)
    if a_min == a[0]:
        a_min = a[n-1]
    c = 0
    for i in range(1, n):
        if a[i] == a[i-1]:
            c += 1
    if c > a_min:
        print("NO")
    else:
        print("YES")


if __name__ == "__main__":
    main()

def solve_problem(n, a):
    max_a = max(a)
    min_a = min(a)
    diff = max_a - min_a
    if diff == 0:
        return 'YES'
    elif diff % 2 == 0:
        return 'YES'
    else:
        return 'NO'


if __name__ == '__main__':
    n = int(input())
    a = input().split()
    for i in range(n):
        a[i] = int(a[i])

    print(solve_problem(n, a))

def check_wall(lst):
    for i in range(len(lst)-1):
        if lst[i] != lst[i+1]:
            return False
    return True

def check_bricks(lst):
    for i in range(len(lst)-1):
        if lst[i] == lst[i+1]:
            return True
    return False

def check_bricks_sum(lst):
    sum_ = 0
    for i in range(len(lst)-1):
        if lst[i] == lst[i+1]:
            sum_ += abs(lst[i] - max(lst))
    return sum_

def check_bricks_sum2(lst):
    sum_ = 0
    max_ = max(lst)
    for i in range(len(lst)):
        if lst[i] == max_:
            continue
        else:
            sum_ += abs(max_ - lst[i])
    return sum_

def main():
    n = int(input())
    lst = list(map(int, input().split()))
    if check_wall(lst):
        print("YES")
    elif check_bricks(lst):
        print("YES")
    else:
        print("NO")

if __name__ == "__main__":
    main()
