import math
import sys

def get_number_of_possible_tuples(n):
    number_of_tuples = 0
    for a in range(1, math.ceil(math.sqrt(n))):
        b = (n - a) / (a + 1)
        if b == int(b):
            number_of_tuples += 1
    return number_of_tuples

def main():
    n = int(input())
    print(get_number_of_possible_tuples(n))

if __name__ == '__main__':
    main()

# This is a simple problem, but it is important to note that the number of
# tuples is equal to the number of divisors of N.

N = int(input())

def count_divisors(N):
    count = 0
    for i in range(1, N+1):
        if N % i == 0:
            count += 1
    return count

print(count_divisors(N))
