import math

x, k, d = map(int,input().split())

if k*d > abs(x):
    k = abs(x) // d
    x -= k*d
    if abs(x) % d == 0:
        print(abs(x))
    else:
        print(abs(x) - d)
else:
    print(abs(x - k*d))

def main():
    x, k, d = map(int, input().split())
    if abs(x) > k * d:
        print(abs(x) - k * d)
    else:
        if (abs(x) % d) + (k - (abs(x) // d)) * d < (k - (abs(x) // d)) * d:
            print(abs(x) % d)
        else:
            print(abs((k - (abs(x) // d)) * d - abs(x) % d))


if __name__ == '__main__':
    main()

def solve(x, k, d):
    # k回の移動でxを0に近づける
    if abs(x) >= k * d:
        return abs(x) - k * d
    # どのように移動しても、0に到達しない場合
    if (k - abs(x) // d) % 2 == 1:
        return abs(abs(x) % d - d)
    return abs(x) % d


if __name__ == '__main__':
    x, k, d = map(int, input().split())
    print(solve(x, k, d))
