def solution(year):
    return 1 if (year % 4 == 0 and year % 100 != 0) or year % 400 == 0 else 0

if __name__ == "__main__":
    print(solution(int(input())))

def leap_year(n):
    return 1 if (n % 4 == 0 and n % 100 != 0) or n % 400 == 0 else 0

if __name__ == "__main__":
    print(leap_year(int(input())))


