    Gets all primes less than n
    """
    primes = [2]
    for i in range(3, n+1, 2):
        is_prime = True
        for j in range(3, i, 2):
            if i % j == 0:
                is_prime = False
                break
        if is_prime:
            primes.append(i)
    return primes

def get_factors(n, primes):
    """
    Gets all factors of n
    """
    factors = []
    for p in primes:
        if p * p > n:
            break
        while n % p == 0:
            factors.append(p)
            n //= p
    if n > 1:
        factors.append(n)
    return factors

def get_divisors(n, primes):
    """
    Gets all divisors of n
    """
    factors = get_factors(n, primes)
    n_factors = len(factors)
    divisors = set([1, n])
    for i in range(1 << n_factors):
        divisor = 1
        for j in range(n_factors):
            if i & 1 << j:
                divisor *= factors[j]
        divisors.add(divisor)
    return divisors

def get_missing_divisors(divisors, n):
    """
    Gets all divisors of n that are not in divisors, excluding 1
    """
    missing = set(divisors) - set(n for n in range(1, n+1))
    missing.remove(1)
    return missing

