    """
    Gets the smallest number that has divisors as its divisors, or -1 if no such number exists
    """
    primes = get_primes(max(divisors))
    for d in divisors:
        missing = get_missing_divisors(get_divisors(d, primes), divisors)
        if len(missing) == 1:
            return d * missing.pop()
    return -1

