def main():
    n = int(input())
    a = list(map(int, input().split()))
    for i in range(n):
        for j in range(i+1, n):
            if a[i] == a[j]:
                a[j] = 0
    a = list(set(a))
    a.sort(reverse=True)
    print(len(a))
    print(' '.join(map(str, a)))


if __name__ == '__main__':
    main()

# Solution

n = int(input())
arr = [int(x) for x in input().split()]
d = {}
for i in range(n):
    if arr[i] not in d.keys():
        d[arr[i]] = 1
    else:
        d[arr[i]] += 1

new_arr = []
for k, v in d.items():
    new_arr.append(k)

print(len(new_arr))
for i in new_arr:
    print(i, end=" ")
