import math

def binarySearch(n, x, pos):
    if pos == 0:
        return math.factorial(n-1)
    elif pos == n-1:
        return math.factorial(n-1)
    else:
        return 2 * math.factorial(n-2)

def main():
    n, x, pos = map(int, input().split())
    print(binarySearch(n, x, pos) % (10**9 + 7))

if __name__ == "__main__":
    main()
