def main():
    n = int(input())
    s = input()
    t = input()
    result = 0
    for i in range(n):
        if s[i] != t[i]:
            if s[i+1:] == t[i+1:]:
                result += 1
            elif s[i:] == t[i+1:]:
                result += 1
            else:
                break
    print(result)

if __name__ == "__main__":
    main()
