n = int(input())
a = list(map(int, input().split()))
print(max(a) - min(a) - 2 * min(max(a) - a.index(max(a)) - 1, a.index(min(a)) - 1)) 

def main():
    n = int(input())
    a = [int(i) for i in input().split()]
    a.sort()
    min_diff = a[n - 1] - a[0]
    for i in range(n):
        if i == 0:
            diff = a[i + 1] - a[i]
        elif i == n - 1:
            diff = a[i] - a[i - 1]
        else:
            diff = min(a[i] - a[i - 1], a[i + 1] - a[i])
        if diff < min_diff:
            min_diff = diff
    print(min_diff)


if __name__ == "__main__":
    main()

