def main():
    n, r = map(int, input().split())  # n: number of projects, r: initial rating
    projects = []
    for i in range(n):
        projects.append(list(map(int, input().split())))
    projects.sort(key=lambda x: x[0])
    for a, b in projects:
        if r < a:  # if rating is less than the minimum rating required
            print("NO")
            return
        else:
            r += b
    print("YES")


