from sys import stdin
from collections import defaultdict

def solve(n, arr):
    sums = defaultdict(list)
    for i in range(n):
        for j in range(i, n):
            s = sum(arr[i:j+1])
            sums[s].append((i+1, j+1))
    best = max(sums.keys())
    return sums[best]

if __name__ == '__main__':
    n = int(stdin.readline())
    arr = [int(x) for x in stdin.readline().split()]
    print(len(solve(n, arr)))
    for (l, r) in solve(n, arr):
        print(l, r)

import sys

def main():
    n = int(sys.stdin.readline())
    arr = [int(x) for x in sys.stdin.readline().split()]
    result = []
    i = 0
    while i < n:
        j = i + 1
        while j < n:
            if arr[i] == arr[j]:
                result.append([i, j])
                i = j
                j = i + 1
            else:
                j += 1
        result.append([i, i])
        i += 1
    print(len(result))
    for r in result:
        print(str(r[0] + 1) + " " + str(r[1] + 1))

if __name__ == "__main__":
    main()
