def main():
    n, r = map(int, input().split())
    a = list(map(int, input().split()))
    ans = 0
    for i in range(n):
        if a[i] == 1:
            continue
        for j in range(1, r + 1):
            if i - j < 0 or i + j >= n:
                break
            if a[i - j] == 1 or a[i + j] == 1:
                a[i] = 1
                ans += 1
                break
        else:
            print(-1)
            return
    print(ans)

main()

def solve(n, r, heaters):
    heaters = [i for i, x in enumerate(heaters) if x == 1]
    heaters.sort()
    if not heaters:
        return -1
    if heaters[0] > r - 1:
        return -1
    if heaters[-1] < n - r:
        return -1
    count = 1
    for i in range(1, len(heaters)):
        if heaters[i] - heaters[i - 1] > 2 * r - 1:
            count += 1
    return count

n, r = map(int, input().split())
heaters = list(map(int, input().split()))
print(solve(n, r, heaters))
