def solve(n, b, a, s):
    ans = 0
    cur_b, cur_a = b, a
    for i in range(n):
        if s[i]:
            if cur_a:
                cur_a -= 1
            elif cur_b:
                cur_b -= 1
        else:
            if cur_b:
                cur_b -= 1
            elif cur_a:
                cur_a -= 1
        if cur_b or cur_a:
            ans += 1
        else:
            break
    return ans

n, b, a = map(int, input().split())
s = list(map(int, input().split()))
print(solve(n, b, a, s))

n, b, a = map(int, input().split())
s = list(map(int, input().split()))

battery = b
accumulator = a

segments = 0

for i in range(n):
    if s[i] == 1:
        if battery < b:
            battery += 1
        else:
            accumulator += 1
    if battery > 0:
        battery -= 1
        segments += 1
    elif accumulator > 0:
        accumulator -= 1
        segments += 1
    else:
        break

print(segments)
