from collections import Counter

def is_anagram(str1, str2):
 return Counter(str1) == Counter(str2)


s=input()
st=input()
s=s.split(" ")
i=0
c=""
for sr in s:
 i+=1
 if sr!=st and is_anagram(sr, st):
  c=c+str(i)

print("The antidote is found in " + c+ '.')

s = input()
k = input()
wrds = s.split(' ')
kl = len(k)
comp = sorted(k)

def ana(an):
 if an == k:
  return False
 return comp == sorted(an)

ans = ''
for x in range(0, len(wrds)):
 word = wrds[x]
 if len(word) == kl and ana(word):
  ans += str(x+1)

print('The antidote is found in %s.' %ans)
