def rek(a,b,n):

    if a==n and b==n:
        return 1
    if a==n:
        return 1+rek(a,b+1,n)
    if b==n:
        return 1+rek(a+1,b,n)
    return 1+rek(a+1,b,n)+rek(a,b+1,n)

