import sys
input = sys.stdin.readline


t = int(input())
for _ in range(t):
    n = int(input())
    a = list(map(int, input().split()))

    ans = 0
    for i in range(n - 1):
        diff = a[i] - a[i + 1]
        if diff <= 0:
            continue
        else:
            ans = max(len(bin(diff)) - 2, ans)
            a[i + 1] = a[i]
    print(ans)
for t in range(int(input())):
    n = int(input())
    a = [int(i) for i in input().split()]
    m = a[0]
    v = 0
    for i in a:
        v = max(v,m-i)
        m = max(m,i)
    if v == 0:
        print(0)
    else:
        p = 1
        c = 0
        while p<=v:
            p *= 2
            c += 1
        print(c)
for _ in range(int(input())):
    n = int(input())
    l1 = list(map(int, input().split()))
    answer = 0
    max_so_far = l1[0]
    for i in range(1, n):
        if l1[i] >= max_so_far:
            max_so_far = l1[i]
        else:
            answer = max(answer, (max_so_far - l1[i]).bit_length())


    print(answer)
