for _ in range(int(input())):
    n=int(input())
    q=n//4
    r=n%4
    a=-1
    if r==0:
        a=q
    else:
        a=q+1
    print("9"*(n-a)+"8"*a)

for _ in range(int(input())):
    n=int(input())
    count=0
    ans=["" for i in range(n)]
    for i in range(n):
        if count>=3*n:
            ans[i]="8"
        elif 3*n-count>=4:
            ans[i]="9"
            count+=4
        else:
            ans[i]="8"
            count+=4
    print("".join(ans))
