def go():
    n,s,t=int(input()),input(),input()
    if Counter(s)!=Counter(t): return -1
    ans=0
    for i in range(n):
        k=0
        for j in range(i,n):
            while k<n and s[k] != t[j]:
                k += 1
            if k == n: break
            k += 1
            ans = max(ans, j-i+1)
