class Solution:
    def isBoomerang(self, points: List[List[int]]) -> bool:
        x1, y1 = points[0]
        x2, y2 = points[1]
        x3, y3 = points[2]

        return (y2 - y1) * (x3 - x1) != (y3 - y1) * (x2 - x1)

class Solution:
    def isBoomerang(self, points: List[List[int]]) -> bool:
        return points[0][0] * (points[1][1] - points[2][1]) + points[1][0] * (points[2][1] - points[0][1]) + points[2][0] * (points[0][1] - points[1][1]) != 0 #determinant is non-zero

class Solution:
    def isBoomerang(self, points: List[List[int]]) -> bool:
        x1, y1 = points[0]
        x2, y2 = points[1]
        x3, y3 = points[2]

        return (y1 - y2) * (x1 - x3) != (y1 - y3) * (x1 - x2)
