def split_the_bill(x):
    diff = sum(x.values())/float(len(x))
    return {k: round(x[k]-diff, 2) for k in x}

def split_the_bill(spendings):
    fair_amount = sum(spendings.values()) / float(len(spendings))
    return {name: round(amount - fair_amount, 2) for name, amount in spendings.items()}

def split_the_bill(d):
    avg = sum(d.values())/len(d)
    return {n: round(v-avg,2) for n,v in d.items()}

def split_the_bill(x):
    bill = float(sum(amount for amount in x.values())) / len(x)
    return {person : round(amount - bill, 2) for person, amount in x.items()}

def split_the_bill(group):
    mean = sum(group.values()) / float(len(group))
    return {k: round(v - mean, 2) for k, v in group.items()}

