def find_screen_height(width, ratio): 
    a, b = map(int, ratio.split(":"))
    return f"{width}x{int(width / a * b)}"

from fractions import Fraction

def find_screen_height(width, ratio): 
    ratio = ratio.replace(":","/")
    ratio = float(sum(Fraction(s) for s in ratio.split()))
    return "{}x{}".format(width, int(width/ratio))

def find_screen_height(width, ratio): 
    a, b = map(int, ratio.split(':'))
    return f'{width}x{width * b // a}'

def find_screen_height(width, ratio): 
    r = ratio.split(':')
    return f'{width}x{int(width * int(r[1]) / int(r[0]))}'

def find_screen_height(width, ratio):
    r = list(map(int, ratio.split(':')))
    return f'{width}x{width * r[1]//r[0]}'

def find_screen_height(width, ratio): 
    wr, rr = (int(n) for n in ratio.split(":"))
    return f"{width}x{width * rr // wr}"
