    if 1<=a<=10 and 1<=b<=59 : a, b = 11-a, 60-b
    elif 11<=a<=12 and 1<=b<=59 : a, b = 23-a,60-b
    else : a, b = 12-a,b
    return '{0:>02}:{1:>02}'.format(a or 12,b)

def what_is_the_time(time_in_mirror):
    if not time_in_mirror: return
    x, y = map(int, time_in_mirror.split(':'))
    q, r = divmod(720-60*x-y, 60)
    return "{:02d}:{:02d}".format(q%12 or 12, r)

def what_is_the_time(time_in_mirror):
    hours, minutes = map(int, time_in_mirror.split(':'))
    m_hours, m_minutes = divmod(720 - 60 * hours - minutes, 60)
    return '{:02}:{:02}'.format(m_hours + 12 * (m_hours <= 0), m_minutes)
