import re
def is_vowel(s): 
    regex = r'^[aeiou]{1}$'
    return bool(re.match(regex, re.escape(s), re.IGNORECASE))

import re

def is_vowel(stg): 
    return bool(re.fullmatch(r"[aeiou]", stg, re.I))

import re

def is_vowel(s):
    return True if re.match(r"^[AaEeIiOoUu](?!\n)$", s) else False

import re
PATTERN = re.compile('[eaiou]', flags=re.I)
def is_vowel(s): 
    return bool(PATTERN.fullmatch(s))

