from re import sub
def vowel_start(st):
    return sub(r'(?<=.)([aeiou])', r' \1', sub(r'[^a-z0-9]', '', st.lower()))

def vowel_start(s): 
    r = ''
    for letter in s:
        if letter in 'aeiouAEIOU':
            r+=' '+letter.lower()
        elif letter.isalnum():
            r+=letter.lower()
    return r.lstrip(' ')

from string import ascii_lowercase, digits

def vowel_start(st): 
    return ''.join([
        (' ' + c) if c in 'aeiou' else c 
        for c in st.lower() 
        if c in ascii_lowercase + digits
    ]).lstrip()

from re import sub

def vowel_start(st): 
    return sub(r"\B([aeiou])", r" \1", ''.join(filter(str.isalnum, st)).lower())

def vowel_start(st): 
    rst = ''
    for e in st:
        if not e.isalnum(): continue
        e = e.lower()
        if e in 'aeiou':
            rst += f' {e}'
        else:
            rst += e
    return rst.lstrip()
