def unflatten(flat_array):
    arr = flat_array[:]
    for i, v in enumerate(arr):
        if v > 2:
            arr[i], arr[i+1:i+v] = arr[i:i+v], []
    return arr

def unflatten(flat):
    if not flat :  return []
    if flat[0]<3 : return [flat[0]] + unflatten(flat[1:])
    else:          return [ flat[0:flat[0]] ] + unflatten(flat[flat[0]:])

