def find_all(array, n):
    res = []
    for i in range(len(array)):
        if array[i] == n:
            res.append(i)
    return res

def find_all(array, n):
    l = []
    for i,v in enumerate(array):
        if v == n:
            l.append(i)
    return l

import numpy as np

def find_all(array, n):
    return list(np.where(np.array(array) == n)[0])
