def short_form(s):
    n = len(s)
    return ''.join(c for i, c in enumerate(s) if c not in 'aeiouAEIOU' or (i == 0 or i == n - 1))

import re
def short_form(s):
    return s[0] + re.sub(r'[aeiou]', '', s[1:-1], flags=re.I) + s[-1]
