def vaporcode(s):
    string = ''
    s = ''.join(s.split()).upper()
    for c in s:
        string += c + '  '
    return string[:-2]

from string import ascii_lowercase, ascii_uppercase
trans = str.maketrans(ascii_lowercase, ascii_uppercase, ' ')

def vaporcode(s):
    return "  ".join(s.translate(trans))

def vaporcode(s):
    vapor_s = ''.join(['{}  '.format(i) for i in s.upper().replace(' ','')])
    return vapor_s.rstrip()

def vaporcode(s):
    result = ''
    for x in s:
        if x == ' ':
            continue
        result = result + x.upper() + '  '
    return result[:len(result)-2]
