def ones_counter(xs):
    return [sum(g) for x, g in groupby(xs) if x == 1]

def ones_counter(input):
    c, result = 0, []
    for x in input + [0]:
        if x != 1 and c != 0:
            result.append(c)
            c = 0
        elif x == 1:
            c += 1
    return result

def ones_counter(a):
    r = [0]
    for x in a:
        if x == 1:
            r[-1] += 1
        else:
            if r[-1]: r.append(0)
    return [x for x in r if x]
