def pig_it(text):
    lst = text.split()
    return ' '.join( [word[1:] + word[:1] + 'ay' if word.isalpha() else word for word in lst])

def pig_it(text):
    res = []

    for i in text.split():
        if i.isalpha():
            res.append(i[1:]+i[0]+'ay')
        else:
            res.append(i)

    return ' '.join(res)

def pig_it(text):
    new_string = ""
    for each in text.split():
        if each == "?": 
            new_string += "? "
        elif each == "!":
            new_string += "! "
        else:    
            new_string += each[1:len(each)] + each[0] + "ay "
    a= len(new_string)    
    return new_string[0:a-1]

import re

def pig_it(text):
    return re.sub(r'([a-z])([a-z]*)', r'\2\1ay', text, flags=re.I)

import re
def pig_it(text):
    return re.sub(r'(\w{1})(\w*)', r'\2\1ay', text)

from string import punctuation
def pig_it(text):
    words = text.split(' ')
    return ' '.join(
        [
            '{}{}ay'.format(
                word[1:],
                word[0]
            ) if word not in punctuation else word for word in words
        ]
    )

def pig_it(text):
    piggy = []
    for word in text.split():
        if word.isalpha():
            piggy.append(word[1:]+ word[0] + "ay")
        else:
            piggy.append(word)
    return ' '.join(piggy)
