def longest(words):
    number_of_chars = [len(word) for word in words]
    return max(number_of_chars)

def longest(words):
    longest=words[0]
    for i in range(len(words)):
        if len(words[i])>len(longest):
            longest = words[i]
    return len(longest)

def longest(words):
    biggest = 0
    for word in words:
        if len(word) > biggest:
            biggest = len(word)
    return biggest

def longest(words):
    sorted_list = sorted(words, key=len, reverse=True)
    return len(sorted_list[0])

def longest(words):
    lon = len(words[0])
    for i in words:
        if len(i)>lon:
            lon = len(i)
    return lon
