def product(s):
    ce = cq = 0
    for i in s:
        if i == '!':
            ce += 1
        elif i == '?':
            cq += 1
    return ce * cq

from collections import Counter


def product(s: str) -> int:
    c = Counter(s)
    return c.get('!', 0) * c.get('?', 0)

import operator

def product(s):
    return operator.mul(*list(map(s.count, '?!')))

