def aa_percentage(prot, rsd="AILMFWYV"):
    rsd = set(rsd)
    return round(sum(p in rsd for p in prot)/len(prot)*100)

def aa_percentage(*prot):
    if len(prot)>1:
        sear = prot[1]
    else:
        sear =  ["A", "I", "L", "M", "F", "W", "Y", "V"]
    times = 0
    for i in sear:
        times += prot[0].count(i)
    return round((times / len(prot[0])) * 100)

def aa_percentage(seq,codes="AILMFWYV"):
    codes=set(codes)
    return round(sum(c in codes for c in seq)/len(seq)*100)

def aa_percentage(sequence,list = ["A", "I", "L", "M", "F", "W", "Y", "V"]):
    counts = [sequence.count(x) for x in list]
    return round(sum(counts)*100/len(sequence),0)

