def shuffled_array(s):
    for i in s:
        if sum(s)-i == i: 
            s.pop(s.index(i))
            break
    return sorted(s)

def shuffled_array(s):
    for i in range(len(s)):
        k = s[:i] + s[i+1:]
        if sum(k) == s[i]:
            return sorted(k)

def shuffled_array(s):
    s.sort()
    for i in range(len(s)):
        if s[i] == sum(s[:i]+s[i+1:]):
            return s[:i]+s[i+1:]

def shuffled_array(lst):
    R = lst[:]
    R.remove(sum(R) // 2)
    return sorted(R)
