def game(n):
    m = n * n
    if (m % 2 == 0):
        return [m // 2] 
    else: return [m, 2]

def game(n):
    numerator = n ** 2
    denominator = 2
    if numerator % denominator == 1:
        return [numerator, denominator]
    return [numerator // denominator]

