def a(n):
    """
    """
    if n % 2 != 0:
        n = n - 1
    if n < 4:
        return ''
    side = " " * (n - 1)
    li = [side + "A" + side]
    for i in range(1, n):
        side = side[1:]
        middle = "A " * (i - 1) if i == (n / 2) else "  " * (i - 1)
        li.append(side + "A " + middle + "A" + side)
    return "\n".join(li)

def a(n):
    if n < 4:
        return ""
    if n % 2:
        n -= 1
    lines = (
        "A" if i == 0 else
        " ".join("A" * (i + 1)) if i == n // 2 else
        "A" + " " * (2 * i - 1) + "A"
        for i in range(n)
    )
    width = 2 * n - 1
    return "\n".join(line.center(width) for line in lines)

def a(n):
    n -= n%2
    if n<4: return ''
    return '\n'.join( '{0}{1}{0}'.format('A' * (i%(n//2) != 0),
                                         ' ' * (i*2-1) if i%(n//2) else ' '.join( ['A']*(i+1) )
                                        ).center(2*n-1) for i in range(n) )

def a(n):
    if n<4: return ''
