import re

def consonant_count(s):
    return len(re.sub(r'[aeiou\d\s\W\_]', '', s))

import re

def consonant_count(s):
    return len(re.sub(r'[^b-df-hj-np-tv-z]', '', s, flags=re.I))

VOWELS = set('aeiouAEIOU')


def consonant_count(s):
    return sum(1 for a in s if a.isalpha() and a not in VOWELS)

from collections import Counter

def consonant_count(str):
    return sum(c for x, c in Counter(str.lower()).items() if x in 'bcdfghjklmnpqrstvwxyz')
