def single_digit(n):
    while n > 9:
        n = bin(n).count("1")
    return n

def single_digit(n):
    while n > 9:
        n = int(bin(n).count('1'))
    return n

def single_digit(n):
    if len(str(n)) == 1:
        return n
    else:
        return single_digit(bin(n)[2:].count('1'))

def single_digit(n):
    while n > 9:
        n = sum(1 if i == "1" else 0 for i in bin(n))
    return n

def single_digit(n):
    while n/10>=1:
        n=sum(map(int, bin(n)[2:]))
    return n

def single_digit(n):
    while n >= 10:
        n = sum(c == '1' for c in bin(n))
    return n

def single_digit(n):
    if n < 10:
        return n
    return single_digit(bin(n).count('1'))
