def is_very_even_number(n):
    while len(str(n)) > 1:
        n = sum(int(x) for x in str(n))
    return True if n % 2 == 0 else False

def is_very_even_number(n):
    if n < 10:
        return n % 2 == 0

    return is_very_even_number(sum(int(d) for d in str(n)))

def get_sum(x):
    return sum(int(t) for t in list(str(x)))

def is_very_even_number(n):
  result = get_sum(n)
  while len(str(result)) >= 2:
    result = get_sum(result)
  return (result %2 == 0)

def is_very_even_number(n):
    while n>=10:
        p, n = n, 0
        while p>0:
            re = p%10
            n += re
            p //= 10
    return n%2==0

def is_very_even_number(n):
    if len(str(n)) == 1:
        return n % 2 == 0
    return is_very_even_number(sum(map(int, list(str(n)))))

def is_very_even_number(n):
