class FileMaster():
    def __init__(self, filepath):
        lk = filepath.rfind('.')
        ls = filepath.rfind('/')
        self.ext = filepath[lk+1:]
        self.file = filepath[ls+1:lk]
        self.path = filepath[:ls+1]
    def extension(self):
        return self.ext
    def filename(self):
        return self.file

    def dirpath(self):
        return self.path

class FileMaster():
    def __init__(self, filepath):
        self.fp = filepath

    def extension(self):
        return self.fp.split('.')[-1]

    def filename(self):
        return self.fp.rsplit('/', 1)[-1].split('.')[0]

    def dirpath(self):
        return self.fp.rsplit('/', 1)[0] + '/'

class FileMaster():
    def __init__(self, filepath):
        self.f_file = filepath.split('/')
        self.f_name, self.extn = self.f_file[-1].split('.')

    def extension(self):
        return self.extn

    def filename(self):
        return self.f_name

    def dirpath(self):
        return '/'.join(self.f_file[:-1]) + '/'

class FileMaster():
    def __init__(self, filepath):
        self.path=filepath
        self.dot=self.path.index(".")
        self.slash=self.path[::-1].index("/")
    def extension(self):
        return self.path[self.dot+1:]
    def filename(self):
        return self.path[-self.slash:self.dot]
    def dirpath(self):
        return self.path[:-self.slash]

