class FileMaster():
    def __init__(self, filepath):
        self.filepath = filepath
    def extension(self):
        return self.filepath.rsplit('.', 1)[-1]
    def filename(self):
        return self.filepath.rsplit('/', 1)[-1].rsplit('.', 1)[0]
    def dirpath(self):
        return self.filepath[:self.filepath.rfind('/')+1]

class FileMaster():
    def __init__(self, filepath):
        self.path = filepath
        self._start_path = filepath.split('/')[:-1]
        self._end_path = filepath.split('/')[-1].split('.')
    def extension(self):
        return self._end_path[1]
    def filename(self):
        return self._end_path[0]
    def dirpath(self):
        return '/'.join(self._start_path)+'/'

class FileMaster():
    def __init__(self, filepath):
        self.path=filepath
    def extension(self):
        return self.path[self.path.index(".")+1:]
    def filename(self):
        return self.path[-self.path[::-1].index("/"):self.path.index(".")]
    def dirpath(self):
        return self.path[:-self.path[::-1].index("/")]

class FileMaster():
    def __init__(self, filepath):
        self.arr = filepath.split('/')
    def extension(self):
        return self.arr[-1].split('.')[1]
    def filename(self):
        return self.arr[-1].split('.')[0]
    def dirpath(self):
        self.arr[-1] = ''
        return '/'.join(self.arr)
