import random

def random_case(x):
    return "".join([random.choice([c.lower(), c.upper()]) for c in x])

from random import choice

UP_LOW = (str.upper, str.lower)


def random_case(strng):
    return ''.join(choice(UP_LOW)(a) for a in strng)

import random

def random_case(x):
    result = ""

    for c in x:
        if random.randint(0, 1) == 1:
            result += c.upper()
        else:
            result += c.lower()

    return result

from random import choice


def random_case(x):
    cases = (str.lower, str.upper)
    return ''.join(choice(cases)(char) for char in x)

