from collections import Counter

def count_char(s, c):
    return Counter(s.lower())[c.lower()]

def count_char(string, letter):
    s = string.lower()
    l = letter.lower()
    return s.count(l)

def count_char(line, c):
    line = line.lower()
    return line.count(c.lower())

def count_char(a,b):
    a=a.lower()
    b=b.lower()
    return a.count(b)
