from datetime import date


def days_until_christmas(day):
    christmas = date(day.year, 12, 25)
    if day > christmas:
        christmas = date(day.year + 1, 12, 25)
    return (christmas - day).days

def days_until_christmas(day):
    christmas = date(day.year + ((day.month, day.day) > (12, 25)), 12, 25)
    return (christmas - day).days

from datetime import date
def days_until_christmas(day):
    return (date(day.year + (day.month == 12 and day.day > 25), 12, 25) - day).days

def days_until_christmas(day):
    CHRISTMAS = date(day.year,12,25)
    if day > CHRISTMAS:
        CHRISTMAS = CHRISTMAS.replace(year=day.year + 1)
    return (CHRISTMAS - day).days
