def shuffle_it(A,*T):
    for x,y in T:
        A[x],A[y]=A[y],A[x]
    return A

def shuffle_it(arr, *ind):
    for a, b in ind:
        [arr[b],arr[a]] = [arr[a],arr[b]]
    return arr

def shuffle_it(a, *args):
    for x, y in args:
        a[x], a[y] = a[y], a[x]
    return a

def shuffle_it(a,*b):
    r=a[:]
    for x,y in b:
        r[x],r[y]=r[y],r[x]
    return r

def shuffle_it(xs, *nss):
    for i, j in nss:
        xs[i], xs[j] = xs[j], xs[i]
    return xs

def shuffle_it(arr, *shifts):
    for (a,b) in shifts:
        arr[a], arr[b] = (arr[b], arr[a])
    return arr

def shuffle_it(char,*args):
    for arg in args:
        b = char[arg[0]]
        char[arg[0]] = char[arg[1]]
        char[arg[1]] = b
    return (char)

def shuffle_it(*args):
    arr, *args = args
    for i1, i2 in args:
        arr[i1], arr[i2] = arr[i2], arr[i1]
    return arr

def shuffle_it(arr,*ars):
    for [x,y] in ars:
        arr[x],arr[y]=arr[y],arr[x]
    return arr
