def sort_it(s, n):
  words = s.split(', ')
  return ', '.join( word for word in sorted(words, key=lambda x: x[n-1]) )

def sort_it(list_, n):    
    str_list = list_.split(", ")
    str_list.sort(key=lambda f: f[n - 1])
    return ", " .join(str_list)
