import sys

def solve(n, k, s):
    if k == 0:
        return s

    for i in range(26):
        c = chr(ord('a') + i)
        j = s.find(c)
        if j == -1:
            continue
        if j < k:
            k -= j + 1
            s = s[j + 1:]
        else:
            return s[:j] + s[j + 1:]

    return ''

