#!/usr/bin/env python3

n = int(input())
a = list(map(int, input().split()))

d = [0] * n

for i in range(n - 1, -1, -1):
    if i + a[i] < n:
        d[i] = d[i + a[i]] + 1
    if i - a[i] >= 0:
        d[i] = min(d[i], d[i - a[i]] + 1)
    if d[i] == 0:
        d[i] = -1

print(*d)

import queue

n = int(input())
a = list(map(int, input().split()))

q = queue.Queue()

for i in range(n):
    if a[i] % 2 == 0:
        q.put((i, 0))

for i in range(n):
    if a[i] % 2 == 1:
        q.put((i, 0))

visited = [False] * n

while not q.empty():
    curr, dist = q.get()
    if visited[curr]:
        continue
    visited[curr] = True
    if curr - a[curr] >= 0:
        q.put((curr - a[curr], dist + 1))
    if curr + a[curr] < n:
        q.put((curr + a[curr], dist + 1))

for i in range(n):
    if a[i] % 2 == 0:
        if visited[i]:
            print(dist)
        else:
            print(-1)
    else:
        if visited[i]:
            print(dist)
        else:
            print(-1)
